import React, { useRef, useEffect, useState } from 'react';
import axios from 'axios';

const Player = ({ songId }) => {
	const audioRef = useRef(null);
	const [isPlaying, setIsPlaying] = useState(false);
	const [progress, setProgress] = useState(0);
	const [song, setSong] = useState({});
	const [audioUrl, setAudioUrl] = useState(null);

	useEffect(() => {
		const fetchAudioUrl = async () => {
			try {

				const songDataRes = await axios.get(song.id);
				const songData = songDataRes.data;
				setSong(songData);

				const response = await axios.get(song.url, {
					responseType: 'arraybuffer', // Important to get the raw binary data
				});

				const blob = new Blob([response.data], { type: 'audio/mpeg' }); // Create a Blob from the data
				const url = window.URL.createObjectURL(blob); // Generate a URL for the Blob
				console.clear();
				console.log(blob, "hhhhh");
				setAudioUrl(url);
			} catch (error) {
				console.error('Error fetching the audio file:', error);
			}
		};

		fetchAudioUrl();
	}, [song.id]);

	useEffect(() => {
		if (audioRef.current && audioUrl) {
			audioRef.current.load();
			if (isPlaying) {
				audioRef.current.play();
			}
		}
	}, [audioUrl]);

	const togglePlayPause = () => {
		if (isPlaying) {
			audioRef.current.pause();
		} else {
			audioRef.current.play();
		}
		setIsPlaying(!isPlaying);
	};

	const handleTimeUpdate = () => {
		if (audioRef.current) {
			const currentTime = audioRef.current.currentTime;
			const duration = audioRef.current.duration;
			setProgress((currentTime / duration) * 100);
		}
	};

	const handleSeek = (e) => {
		const seekTime = (e.target.value / 100) * audioRef.current.duration;
		audioRef.current.currentTime = seekTime;
	};


	return (
		<div className="player">
			{audioUrl ? (
				<>
					<audio
						ref={audioRef}
						onTimeUpdate={handleTimeUpdate}
					>
						<source src={audioUrl} type="audio/mpeg" />
						Your browser does not support the audio element.
					</audio>
					<div className="player-controls">
						<button onClick={togglePlayPause}>
							{isPlaying ? 'Pause' : 'Play'}
						</button>
						<input
							type="range"
							value={progress}
							onChange={handleSeek}
							className="seeker"
						/>
					</div>
					<div className="song-info">

						<p>{song.name}</p>
						<p>{song.artist}</p>
					</div>
				</>
			) : (
				<p>Loading...</p>
			)}
		</div>


	);
};

export default Player;
