import React, { useEffect } from 'react';
import './Player.css';

const Player = ({ song }) => {
  useEffect(() => {
    const gradientColors = extractColorsFromImage(`https://cms.samespace.com/assets/${song.cover}`);
    document.body.style.background = `linear-gradient(to bottom, ${gradientColors})`;
  }, [song]);

  const extractColorsFromImage = (imageUrl) => {
    // This function can be more advanced using a library to extract colors
    return ['#ff7e5f', '#feb47b']; // placeholder colors
  };

  return (
    <div className="player">
      <img src={`https://cms.samespace.com/assets/${song.cover}`} alt={song.name} className="cover" />
      <div className="details">
        <h2>{song.name}</h2>
        <h3>{song.artist}</h3>
      </div>
      <div className="controls">
        <button>Previous</button>
        <button>Play/Pause</button>
        <button>Next</button>
      </div>
    </div>
  );
};

export default Player;
