import React, { useState, useEffect } from 'react';
import Player from './components/Player';
import SongList from './components/SongList';
import SearchBar from './components/SearchBar';
import Tabs from './components/Tabs';
import { fetchSongs } from './services/api';
import styled from 'styled-components';

const AppWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background: #1e1e1e;
  min-height: 100vh;
  padding: 20px;
  color: #fff;
`;

const App = () => {
  const [songs, setSongs] = useState([]);
  const [filteredSongs, setFilteredSongs] = useState([]);
  const [currentSong, setCurrentSong] = useState(null);
  const [tab, setTab] = useState('forYou');

  useEffect(() => {
    const loadSongs = async () => {
      const songData = await fetchSongs();
      setSongs(songData);
      setFilteredSongs(songData);
      setCurrentSong(songData[0]);
    };
    loadSongs();
  }, []);

  const handleSearch = (query) => {
    setFilteredSongs(songs.filter((song) => song.title.toLowerCase().includes(query.toLowerCase())));
  };

  const handleTabChange = (tab) => {
    setTab(tab);
    // Handle tab change logic here
  };

  return (
    <AppWrapper>
      <Tabs onTabChange={handleTabChange} />
      <SearchBar onSearch={handleSearch} />
      {currentSong && <Player currentSong={currentSong} />}
      <SongList songs={filteredSongs} onSelectSong={setCurrentSong} />
    </AppWrapper>
  );
};

export default App;
